from flask import Flask, render_template, jsonify, request
from flask import Flask, render_template, redirect
from flask_pymongo import PyMongo

import flask

app = Flask(__name__)


app.config["MONGO_URI"] = "mongodb://localhost:27017/Airbnb_DB"
mongodb_client = PyMongo(app)
results_collection = mongodb_client.db.Rental_Property_Information




# # # # # # # # # # # # # # # # # # # # # # # # # # # # # # # # # # # # # # # # # # # # # # # # # # # #
# # # # # # # # # # # # # # # # # # # # # # # # # # # # # # # # # # # # # # # # # # # # # # # # # # # #
# # # # # # # # # # # # # # # # # # # # # # # # # # # # # # # # # # # # # # # # # # # # # # # # # # # #
@app.route('/')
@app.route('/home')
def home():

    # Find one record of data from the mongo database
    Result_Dict = results_collection.find_one()
    # Return template and data
    return render_template('home.html', airbnb=Result_Dict)


